define(["mod/fund-state-meter"],function(e){var t=function(e,t,n){this.balance=e||0,this.averageDeduction=t||40,this.outlookWeeks=n||4,this.update()};return t.prototype={setBalance:function(e){this.balance=e,this.update()},setAverageDeduction:function(e){this.averageDeduction=e,this.update()},setOutlookWeeks:function(e){this.outlookWeeks=e,this.update()},update:function(){var t=parseFloat(this.balance),n=parseInt(this.averageDeduction,10)*parseInt(this.outlookWeeks,10);e.render(Math.min(Math.max(0,t),n)/n),t=t.toFixed(2).split("."),document.getElementById("fund-balance").innerHTML="$"+t[0]+"<sup>."+t[1]+"</sup>"}},t})